package config

import (
	"encoding/json"
	"io/ioutil"
	"os"
	"testing"
)

func clearConfigFile() {
	os.Remove(configPath)
}

func TestConfigSaveConfig(t *testing.T) {
	clearConfigFile()
	config := Config{
		ListenAddr: ":7474",
		RemoteAddr: "do.wuhaolin.cn:45234",
		Password:   "",
	}
	config.SaveConfig()

	file, err := os.Open(configPath)
	if err != nil {
		t.Errorf("打开配置文件 %s 出错:%s", configPath, err)
	}
	defer file.Close()

	tmp := make(map[string]string)
	err = json.NewDecoder(file).Decode(&tmp)
	if err != nil {
		t.Error(err)
	}

	if tmp["listen"] != ":7474" || tmp["password"] != "" || tmp["remote"] != "do.wuhaolin.cn:45234" {
		t.Error("数据保存后不一致")
	}
}

func TestConfigReadConfig(t *testing.T) {
	clearConfigFile()
	jsonConfig := []byte(`
{
	"listen": ":7474",
	"remote": "do.wuhaolin.cn:45234",
	"password": ""
}`)

	err := ioutil.WriteFile(configPath, jsonConfig, 0644)
	if err != nil {
		t.Error(err)
	}

	config := Config{}
	config.ReadConfig()

	if config.ListenAddr != ":7474" || config.RemoteAddr != "do.wuhaolin.cn:45234" || config.Password != "" {
		t.Error("读取的配置数据不一致")
	}

}
